export default function Game({ selectedGame, setSelectedGame }) {
    if (!selectedGame) return null;

    return (
      <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 backdrop-blur-md">
        <div className="bg-gray-900 mt-6 rounded-lg shadow-lg max-w-lg w-full text-center">
          <img
            src={selectedGame.imageURL}
            alt={selectedGame.title}
            className="mt-4 rounded-lg w-full max-h-48 p-4 object-contain shadow-md"
          />
          <button
            className="mt-4 bg-red-600 text-white px-5 py-2 rounded hover:bg-red-700 transition"
            onClick={() => setSelectedGame(null)}
          >
            Close
          </button>
        </div>
      </div>
    );
  }
