import Game from "./Game"
import { useQuery, gql } from '@apollo/client';

export default function GamesList({games,isDeveloper})
{
    const GAMES_QUERY=`
    getAllGames
    {
    id
    title
    genres{
    name
    }
    }
    `
    const { data, loading, error } = useQuery(GAMES_QUERY);


    if(!isDeveloper)
    {
    if (loading) return <p className="text-center text-gray-600">Loading developers...</p>;
    if (error) return <p className="text-center text-red-500">Error loading data</p>;
     return (
        <div className="min-h-screen bg-gray-900 py-10 mb-5">
          <div className="max-w-7xl mx-auto px-6">
            <h1 className="text-4xl font-bold text-center text-white mb-8">
              Games
            </h1>

            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8">
              {data?.getAllGames.map((game) => (
                <Game key={game.id} game={game} />
              ))}
            </div>
          </div>
        </div>
      );
    }
    const gamesList=games.map((game)=>(
        <Game key={game.id} game={game} isDeveloper={isDeveloper}/>
    ))
   return  (<ul  className="mt-4 space-y-2">{gamesList}</ul>)
}
