import { useState } from "react";

export default function Developer({ developer }) {
  const [selectedGame, setSelectedGame] = useState(null);

  return (
    <div className="p-6 max-w-lg mx-auto bg-gray-100 shadow-md rounded-lg mb-6">
      <h1 className="text-2xl font-bold">
        {developer.name} {developer.surname}
      </h1>
      <h2 className="text-gray-600">Games:</h2>
      <ul className="mt-2 space-y-2">
        {developer.games.map((game, index) => (
          <li key={index}>
            <button
              className="text-blue-500 hover:underline"
              onClick={() => setSelectedGame(game)}
            >
              {game.title}
            </button>
          </li>
        ))}
      </ul>

      {/* Game Card Modal */}
      {selectedGame && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg text-center">
            <h3 className="text-lg font-bold">{selectedGame.title}</h3>
            <img
              src={selectedGame.imageURL}
              alt={selectedGame.title}
              className="mt-2 rounded-lg max-w-full h-auto"
            />
            <button
              className="mt-3 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
              onClick={() => setSelectedGame(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
