import { NavLink } from "react-router";
import { useState } from "react";

export default function GamesListDeveloper({ games }) {
  function handleHoveredGame(imageURL) {
    setHoveredGame(imageURL);
  }

  const [hoveredGame, setHoveredGame] = useState(null);

  const gamesList = games.map((game) => (
    <NavLink
      key={game.id}
      to={`/games/details/:${game.id}`}
      className="flex items-center p-2 rounded-md hover:bg-gray-800 transition duration-300 space-x-4" // Flex layout for image and text
      onMouseEnter={() => handleHoveredGame(game.imageURL)}
      onMouseLeave={() => setHoveredGame(null)} // Reset when mouse leaves
    >
      {hoveredGame && (
        <img
          src={hoveredGame}
          alt="Game"
          className="w-16 h-auto" // Adjust the image size as needed
        />
      )}
      <p className="text-gray-400">{game.title}</p>
    </NavLink>
  ));

  return (
    <div>
      <ul className="mt-4 space-y-2">{gamesList}</ul>
    </div>
  );
}
