import { useState } from "react";

export default function Developer({ developer }) {
  const [selectedGame, setSelectedGame] = useState(null);

  return (
    <div className="p-6 bg-gray-800 rounded-lg shadow-lg transition-transform transform hover:scale-105">
      <h1 className="text-xl font-bold text-white">{developer.name} {developer.surname}</h1>
      <h2 className="text-gray-400 text-sm">{developer.userEmail}</h2>

      <h3 className="text-gray-300 mt-4 font-semibold">Games:</h3>
      <ul className="mt-2 space-y-2">
        {developer.games.map((game, index) => (
          <li key={index}>
            <button
              className="text-gray-400 hover:underline font-bold hover:text-blue-500 transition-colors"
              onClick={() => setSelectedGame(game)}
            >
              {game.title}
            </button>
          </li>
        ))}
      </ul>

      {/* Game Modal (Popup) */}
      {selectedGame && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-70 backdrop-blur-md">
          <div className="bg-gray-900  mt-4 rounded-lg shadow-lg max-w-lg w-full text-center">
            {/* <h3 className="text-2xl font-bold text-white">{selectedGame.title}</h3> */}
            <img
              src={selectedGame.imageURL}
              alt={selectedGame.title}
              className="mt-4 rounded-lg w-full max-h-28  p-4 object-contain shadow-md"
            />
            <button
              className="mt-4 bg-red-600 text-white px-5 py-2 rounded hover:bg-red-700 transition"
              onClick={() => setSelectedGame(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
}
