import { useState } from "react";

export default function Game({game,isDeveloper}) {

  const  [selectedGame,setSelectedGame]= useState(null)

  if(isDeveloper)
  {
    return (
        <>
        <li key={game.id}>
        <button
          className="text-gray-400 hover:underline font-bold hover:text-blue-500 transition-colors"
          onClick={() => setSelectedGame(game)}
        >
          {game.title}
        </button>
      </li>
      {selectedGame &&   <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 backdrop-blur-md">
        <div className="bg-gray-900 mt-6 rounded-lg shadow-lg max-w-lg w-full text-center">
          <img
            src={game.imageURL}
            alt={game.title}
            className="mt-4 rounded-lg w-full max-h-48 p-4 object-contain shadow-md"

          />
        {/* <p className="text-gray-600">{game.genre}</p> */}

          <button
            className="mt-4 bg-red-600 text-white px-5 py-2 rounded hover:bg-red-700 transition"
            onClick={() => setSelectedGame(null)}
          >
            Close
          </button>
        </div>
      </div>
      }

      </>
    );


  }
  else 
  {
    return (
            <div className="p-6 bg-gray-800 rounded-lg shadow-lg transition-transform transform hover:scale-105">
              <h1 className="text-xl font-bold text-white">{game.title}</h1>

              <img
            src={game.imageURL}
            alt={game.title}
            className="mt-4 rounded-lg w-full max-h-48 p-4 object-contain shadow-md"

          />
            </div>
    )
  }


  }
