import { useQuery, gql } from '@apollo/client';
import Developer from './Developer.js';

const FEED_QUERY=gql`
{
getAllDevelopers
{
    id
    name
    surname
    userEmail
    games
    {
        title
        imageURL
    }
}
}
`

export default function DeveloperList()
{
    const {data,loading,error}=useQuery(FEED_QUERY)
    if (loading) return <p className="text-center text-gray-600">Loading developers...</p>
    if (error) return <p className="text-center text-red-500">Error loading data</p>

    return (
        <div className='min-h-screen min-w-fit justify-normal bg-gray-200 py-10'>
            <div className='max-w-5xl mx-auto px-9 -my-50 ' >
            <h1 className='text-3xl font-bold text-center text-gray-800 mb-6 border border-red-500'>Developers</h1>
            <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6'>
                    {data && (
                <>
                {data.getAllDevelopers.map((developer=>(
                    <Developer key={developer.id} developer={developer}/>
                )))}
                </>
            )}
                    </div>
                </div>
        </div>
    )
}
