import { useApolloClient } from "@apollo/client";
import DeveloperForm from "../components/DeveloperForm";
import client from "../utils/apolloClient";
import { gql } from "@apollo/client";

const CREATE_NEW_DEVELOPER_MUTATION=gql`
mutation createDeveloper($name: String!, $surname: String!, $userEmail: String!, $gameTitle: String!, $imageURL: String!, $gameGenre: String!) {
  createDeveloper(name: $name, surname: $surname, email: $userEmail, gameTitle: $gameTitle, imageURL: $imageURL, gameGenre: $gameGenre) {
    name
    surname
    userEmail
    games {
      title
      genres {
        name
      }
    }
  }
}
`
export default function NewDeveloperPage()
{
    const client=useApolloClient()

return <DeveloperForm/>
}

export async function action({ request,params }) {
    const data = await request.formData();
    const developerData = {
      name: data.get("name"),
      surname: data.get("surname"),
      userEmail: data.get("email"),
      gameTitle: JSON.parse(data.get("game")).title,
      imageURL: JSON.parse(data.get("game")).imageURL,
      description:JSON.parse(data.get("game")).description,
      gameGenre: JSON.parse(data.get("game")).genre,
    };

    try {
      const response = await client.mutate({
        mutation:(CREATE_NEW_DEVELOPER_MUTATION),
        variables: developerData,
      });
      return response.data.createDeveloper;
    } catch (error) {
      console.error("Error creating developer:", error);
      return { error: error.message };
    }
  }
