import { useQuery, gql } from '@apollo/client';
import Developer from './Developer.js';

const DEVELOPER_QUERY = gql`
{
  getAllDevelopers {
    id
    name
    surname
    userEmail
    games {
    id
      title
      description
      imageURL
      genres{
        name
      }
    }
  }
}
`;

export default function DeveloperList() {
  const { data, loading, error } = useQuery(DEVELOPER_QUERY);
  if (loading) return <p className="text-center text-gray-600">Loading developers...</p>;
  if (error) return <p className="text-center text-red-500">Error loading data</p>;

  return (
    <div className="min-h-screen bg-gray-900 py-10 mb-5">
      <div className="max-w-7xl mx-auto px-6">
        <h1 className="text-4xl font-bold text-center text-white mb-8">
          Game Developers
        </h1>

        <div className='flex flex-wrap gap-4'>
            {data?.getAllDevelopers.map((developer)=>{
                <div className='flex-1'>
                <Developer key={developer.id} developer={developer}/>
                </div>
            })}
        </div>
      </div>
    </div>
  );
}


