import { createQueryPreloader, gql,useAp} from "@apollo/client"
import Game from "../components/Game"
import { useLoaderData } from "react-router";
import { useReadQuery,useApolloClient } from "@apollo/client";
const GET_GAME_BY_ID_QUERY=gql`query getGameById($id:ID!)
{
    getGameById(id:$id)
    {
        title,
        imageURL,
        genres
        {
            id,
            name
        }
    }
}
`

let preloadQuery

export default function GameDetailsPage()
{
    const client=useApolloClient()
    preloadQuery=createQueryPreloader(client)

    const queryRef = useLoaderData();
     const { data } = useReadQuery(queryRef);
    return <Game game={data.getGameById} isSingle={true}/>

}
export async function loader({params})
{

const id=params.gameId.substring(1)
 return  preloadQuery(GET_GAME_BY_ID_QUERY, {variables:{id}})
}
