import { useState } from "react"

export default function Developer(props)
{
    const [selectedGame,setSelectedGame]=useState(null)
    const {developer}=props

    return (
        <div className="p-6 max-w-lg mx-auto">
        <h1 className="text-2xl font-bold">
            {developer.name} {developer.surname}
        </h1>
        <p className="mt-4 text-xl font-semibold">{developer.userEmail}</p>

        <h2 className="mt-4 text-xl font-semibold">Games</h2>

        <ul className="mt-2  space-y-2">
        {developer.games.map((game)=>{
            <li key={index}>
                <button className="text-blue-500 hover:underline" onClick={()=>setSelectedGame(game)}>
                {game.title}
                    </button>
            </li>
        })}
        </ul>

        {selectedGame && 
        (
        <div className="mt-6 bg-white shadow-lg roundend-lg p-4">
            <h3 className="text-lg font-bold">{selectedGame.title}</h3>
            <img src={selectedGame.title} alt={selectedGame.title} className="mt-2 rounded-lg"/>
            <button className="mt-3 bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600" onClick={()=>setSelectedGame(null)}>
                Close
            </button>
        </div>
            )

        }
        </div>

)
}
